function Cells(e,s,l){this.index=e,this.value=0,this.x=Math.floor(e/s),this.noGrids=s,this.y=e%s,this.elem=l,this.hasMoved=!1,this.oldValue=0}Cells.prototype.setHasMoved=function(){return this.value!==this.oldValue?(this.hasMoved=!0,this.oldValue=this.value):this.hasMoved=!1,this.hasMoved},Cells.prototype.setCellValue=function(e){switch(this.value=e,e){case 0:return this.elem.innerHTML="",void(this.elem.style.backgroundColor="#eee4da");case 2:this.elem.style.backgroundColor="#faf8ef";break;case 4:this.elem.style.backgroundColor="#EDE0C8";break;case 8:this.elem.style.backgroundColor="#F2B179";break;case 16:this.elem.style.backgroundColor="#F59563";break;case 32:this.elem.style.backgroundColor="#F67C5F";break;case 64:this.elem.style.backgroundColor="#F65E3B";break;case 128:this.elem.style.backgroundColor="#EDCF72";break;case 256:this.elem.style.backgroundColor="#EDCC61";break;case 512:this.elem.style.backgroundColor="#EDC850";break;case 1024:this.elem.style.backgroundColor="#EDC53F";break;case 2048:this.elem.style.backgroundColor="#EDC22E";break;default:this.elem.innerHTML="",this.elem.style.backgroundColor="#eee4da"}this.elem.innerHTML=e};