var EventHandler=function(e){this.grids=e};EventHandler.prototype.keyboardInput=function(){var e=this;window.onkeydown=function(r){switch(r.keyCode){case 37:r.preventDefault(),e.moveLeft();break;case 38:r.preventDefault(),e.moveUp();break;case 39:r.preventDefault(),e.moveRight();break;case 40:r.preventDefault(),e.moveDown();break;default:return}this.grids.cellHasMoved()&&e.grids.randomValue(),console.log(this.grids.cellHasMoved())}},EventHandler.prototype.moveUp=function(){for(var e=this,r=this.grids.noGrids,o=0;r>o;o++){for(var l=[],t=0;r>t;t++)l[t]=e.grids.cellArray[o+4*t].value;for(e.grids.moveCells(l),t=0;r>t;t++)e.grids.cellArray[o+4*t].setCellValue(l[t]?l[t]:0)}},EventHandler.prototype.moveDown=function(){console.log("down key");for(var e=this,r=e.grids.noGrids,o=0;r>o;o++){for(var l=[],t=0;r>t;t++)l[t]=e.grids.cellArray[o+(12-4*t)].value;for(e.grids.moveCells(l),t=0;r>t;t++)e.grids.cellArray[o+(12-4*t)].setCellValue(l[t]?l[t]:0)}},EventHandler.prototype.moveLeft=function(){for(var e=this,r=this.grids.noGrids,o=0;r>o;o++){for(var l=[],t=0;r>t;t++)l[t]=e.grids.cellArray[t+4*o].value;for(e.grids.moveCells(l),t=0;r>t;t++)e.grids.cellArray[t+4*o].setCellValue(l[t]?l[t]:0)}},EventHandler.prototype.moveRight=function(){for(var e=this,r=this.grids.noGrids,o=0;r>o;o++){for(var l=[],t=0;r>t;t++)l[t]=e.grids.cellArray[3-t+4*o].value;for(e.grids.moveCells(l),t=0;r>t;t++)e.grids.cellArray[3-t+4*o].setCellValue(l[t]?l[t]:0)}};