var EventHandler=function(e){this.grids=e};EventHandler.prototype.keyboardInput=function(){var e=this;window.onkeydown=function(r){switch(r.keyCode){case 37:r.preventDefault(),e.moveLeft();break;case 38:r.preventDefault(),e.moveUp();break;case 39:r.preventDefault(),e.moveRight();break;case 40:r.preventDefault(),e.moveDown();break;default:return}}},EventHandler.prototype.moveUp=function(){for(var e=this,r=this.grids.noGrids,o=0;r>o;o++){for(var t=[],n=0;r>n;n++)t[n]=e.grids.cellArray[o+4*n].value;for(e.grids.moveCells(t),n=0;r>n;n++)e.grids.setCellValue(o+4*n,t[n]?t[n]:0)}},EventHandler.prototype.moveDown=function(){console.log("down key");for(var e=this,r=e.grids.noGrids,o=0;r>o;o++){for(var t=[],n=0;r>n;n++)t[n]=e.grids.cellArray[o+(12-4*n)].value;for(e.grids.moveCells(t),n=0;r>n;n++)e.grids.setCellValue(o+(12-4*n),t[n]?t[n]:0)}},EventHandler.prototype.moveLeft=function(){for(var e=this,r=this.grids.noGrids,o=0;r>o;o++){for(var t=[],n=0;r>n;n++)t[n]=e.grids.cellArray[n+4*o].value;for(e.grids.moveCells(t),n=0;r>n;n++)e.grids.setCellValue(n+4*o,t[n]?t[n]:0)}},EventHandler.prototype.moveRight=function(){for(var e=this,r=this.grids.noGrids,o=0;r>o;o++){for(var t=[],n=0;r>n;n++)t[n]=e.grids.cellArray[3-n+4*o].value;for(e.grids.moveCells(t),n=0;r>n;n++)e.grids.setCellValue(3-n+4*o,t[n]?t[n]:0)}};var events=new EventHandler(grids);events.keyboardInput();