var EventHandler=function(e){this.grids=e};EventHandler.prototype.init=function(){this.keyboardInput(),this.mouseEvents()},EventHandler.prototype.keyboardInput=function(){var e=this;window.onkeydown=function(r){switch(r.keyCode){case 37:r.preventDefault(),e.moveLeft();break;case 38:r.preventDefault(),e.moveUp();break;case 39:r.preventDefault(),e.moveRight();break;case 40:r.preventDefault(),e.moveDown();break;default:return}this.grids.cellHasMoved()&&e.grids.randomValue(),console.log(this.grids.cellHasMoved())}},EventHandler.prototype.mouseEvents=function(){var e=this,r=document.getElementById("new-game");r.onclick=function(r){e.grids.resetGame()}},EventHandler.prototype.moveUp=function(){for(var e=this,r=this.grids.noGrids,o=0;r>o;o++){for(var t=[],l=0;r>l;l++)t[l]=e.grids.cellArray[o+4*l].value;for(e.grids.moveCells(t),l=0;r>l;l++)e.grids.cellArray[o+4*l].setCellValue(t[l]?t[l]:0)}},EventHandler.prototype.moveDown=function(){console.log("down key");for(var e=this,r=e.grids.noGrids,o=0;r>o;o++){for(var t=[],l=0;r>l;l++)t[l]=e.grids.cellArray[o+(12-4*l)].value;for(e.grids.moveCells(t),l=0;r>l;l++)e.grids.cellArray[o+(12-4*l)].setCellValue(t[l]?t[l]:0)}},EventHandler.prototype.moveLeft=function(){for(var e=this,r=this.grids.noGrids,o=0;r>o;o++){for(var t=[],l=0;r>l;l++)t[l]=e.grids.cellArray[l+4*o].value;for(e.grids.moveCells(t),l=0;r>l;l++)e.grids.cellArray[l+4*o].setCellValue(t[l]?t[l]:0)}},EventHandler.prototype.moveRight=function(){for(var e=this,r=this.grids.noGrids,o=0;r>o;o++){for(var t=[],l=0;r>l;l++)t[l]=e.grids.cellArray[3-l+4*o].value;for(e.grids.moveCells(t),l=0;r>l;l++)e.grids.cellArray[3-l+4*o].setCellValue(t[l]?t[l]:0)}};