function Grid(r){this.noGrids=NO_GRIDS,this.gridArray=[]}function Cells(r,i){this.index=r,this.value=0,this.x=Math.floor(r/i),this.noGrids=i,this.y=r%i}var NO_GRIDS=4;Grid.prototype.init=function(){for(var r=this,i,t=0;t<r.noGrids*r.noGrids;t++)r.gridArray[t]=new Cells(t,r.noGrids);console.log(r.gridArray)},Grid.prototype.render=function(){for(var r=this,i=[],t=document.getElementById("grids"),n=0;n<r.noGrids;n++){var o=document.createElement("div");o.className="grid-row",t.appendChild(o);for(var d=0;d<r.noGrids;d++){var e=document.createElement("div");e.className="grid-cell",o.appendChild(e),i.push(e)}}return i},Grid.prototype.displayValue=function(){},Grid.prototype.randomValue=function(r,i){return Math.random()<.5?r:i},Grid.prototype.randomIndex=function(){return Math.floor(Math.random()*this.noGrids*this.noGrids+1)},Grid.prototype.setCellValue=function(r,i){this.gridArray[r]=i};var grids=new Grid(NO_GRIDS);grids.init();var m=grids.render();