function Cells(r,e,i){this.index=r,this.value=0,this.x=Math.floor(r/e),this.noGrids=e,this.y=r%e,this.elem=i}var NO_GRIDS=4,Grid=function(r){this.noGrids=NO_GRIDS,this.cellArray=[]};Grid.prototype.init=function(){var r=this,e;this.render(),r.randomValue(),r.randomValue()},Grid.prototype.render=function(){for(var r=this,e=document.getElementById("grids"),i=0;i<r.noGrids;i++){var t=document.createElement("div");t.className="grid-row",e.appendChild(t);for(var n=0;n<r.noGrids;n++){var l=document.createElement("div");l.className="grid-cell",t.appendChild(l),r.cellArray.push(new Cells(i,r.noGrids,l))}}return r.cellArray},Grid.prototype.randomValue=function(){var r=this,e=Math.random()<.5?2:4,i=Math.floor(Math.random()*this.noGrids*this.noGrids);0===r.cellArray[i].value?(console.log("passed: "+i),r.setCellValue(i,e)):(console.log("recursion"),r.randomValue())},Grid.prototype.setCellValue=function(r,e){var i=this.cellArray[r];i.elem.innerHTML=e,i.value=e};var grids=new Grid(NO_GRIDS);grids.init();